import React, { useState } from "react";
import {
  LightModeOutlined,
  DarkModeOutlined,
  Menu as MenuIcon,
  Search,
  ArrowDropDownOutlined,
  ExitToAppOutlined,
} from "@mui/icons-material";
import FlexBetween from "./FlexBetween";

import { useDispatch } from "react-redux";
import { setMode } from "../state/themeSlice";
import {
  alpha,
  AppBar,
  Box,
  Button,
  IconButton,
  InputBase,
  Menu,
  MenuItem,
  styled,
  Toolbar,
  Typography,
  useMediaQuery,
  useTheme,
} from "@mui/material";
import { clearUserLocal } from "../utils";
import { useNavigate } from "react-router-dom";
import SettingsDrawerButton from "./SettingsDrawerButton";

const profileImage =
  "data:image/svg+xml;base64,";

const StyledMenu = styled((props) => (
  <Menu
    elevation={0}
    anchorOrigin={{
      vertical: "bottom",
      horizontal: "right",
    }}
    transformOrigin={{
      vertical: "top",
      horizontal: "right",
    }}
    {...props}
  />
))(({ theme }) => ({
  "& .MuiPaper-root": {
    borderRadius: 0,
    marginTop: theme.spacing(1),
    minWidth: 180,
    color: theme.palette.grey[700],
    backgroundColor: theme.palette.background.alt,
    boxShadow:
      "rgb(255, 255, 255) 0px 0px 0px 0px, rgba(0, 0, 0, 0.05) 0px 0px 0px 1px, rgba(0, 0, 0, 0.1) 0px 10px 15px -3px, rgba(0, 0, 0, 0.05) 0px 4px 6px -2px",
    "& .MuiMenu-list": {
      padding: "4px 0",
    },
    "& .MuiMenuItem-root": {
      "& .MuiSvgIcon-root": {
        fontSize: 18,
        color: theme.palette.text.secondary,
        marginRight: theme.spacing(1.5),
      },
      "&:active": {
        backgroundColor: alpha(
          theme.palette.background.default,
          theme.palette.action.selectedOpacity
        ),
      },
    },
  },
}));

function Navbar({ user, isSidebarOpen, setIsSidebarOpen }) {
  const dispatch = useDispatch();
  const theme = useTheme();
  const isNonMobile = useMediaQuery(theme.breakpoints.up("md"));
  const navigate = useNavigate();

  const [anchorEl, setAnchorEl] = useState(null);
  const isOpen = Boolean(anchorEl);
  const handleUserProfileClick = (event) => setAnchorEl(event.currentTarget);
  const handleUserProfileClose = () => setAnchorEl(null);

  const handleLogout = () => {
    handleUserProfileClose();
    clearUserLocal();
    navigate("/signin");
  };

  return (
    <AppBar
      sx={{
        position: "static",
        background: "none",
        boxShadow: "none",
      }}
    >
      <Toolbar sx={{ justifyContent: "space-between" }}>
        {/* Left Side */}
        <FlexBetween>
          <IconButton onClick={() => setIsSidebarOpen(!isSidebarOpen)}>
            <MenuIcon />
          </IconButton>
          <FlexBetween
            bgcolor={theme.palette.background.alt}
            borderRadius="9px"
            gap="3rem"
            p="0.1rem 1.5rem"
          >
            <InputBase placeholder="Search..." />
            <IconButton>
              <Search />
            </IconButton>
          </FlexBetween>
        </FlexBetween>

        {/* Right Side */}
        <Box sx={{ display: isNonMobile ? "block" : "none" }}>
          <FlexBetween gap="1.5rem">
            <IconButton onClick={() => dispatch(setMode())}>
              {theme.palette.isDark ? (
                <DarkModeOutlined sx={{ fontSize: "25px" }} />
              ) : (
                <LightModeOutlined sx={{ fontSize: "25px" }} />
              )}
            </IconButton>

            <SettingsDrawerButton />

            <FlexBetween>
              <Button
                onClick={handleUserProfileClick}
                sx={{
                  display: "flex",
                  justifyContent: "space-between",
                  alignItems: "center",
                  textTransform: "none",
                  gap: "1rem",
                }}
              >
                <Box
                  component="img"
                  alt="profile"
                  src={user?.profilePic || profileImage}
                  height="40px"
                  width="40px"
                  borderRadius="50%"
                  sx={{ objectFit: "cover" }}
                />
                <Box textAlign="left">
                  <Typography fontWeight="bold" fontSize="0.8rem">
                    {user.name}
                  </Typography>
                  <Typography fontSize="0.7rem">{user.occupation}</Typography>
                </Box>
                <ArrowDropDownOutlined
                  sx={{ color: theme.palette.secondary[900], fontSize: "25px" }}
                />
              </Button>
              <StyledMenu
                anchorEl={anchorEl}
                open={isOpen}
                onClose={handleUserProfileClose}
              >
                <MenuItem onClick={handleLogout}>
                  <ExitToAppOutlined />
                  Logout
                </MenuItem>
              </StyledMenu>
            </FlexBetween>
          </FlexBetween>
        </Box>
      </Toolbar>
    </AppBar>
  );
}

export default Navbar;
